import './App.css';
import {useState,useEffect} from 'react';


const App = () =>  {
  const[count,setCount] = useState(5);
  return (
    <div className="App">
      <>
        <button onClick={()=>{setInterval(() => {setCount((count)=>{
          if(count > 0){
            return count-1;
          }
          else{
            return 0;
          }
        })}, 1000);}}>Start Countdown</button>

        <h1>{count}</h1>
      </>
    </div>
  );
}

export default App;
