import './App.css';
import {useState,useEffect} from 'react';


const App = () =>  {
  const[count,setCount] = useState(60);
  return (
    <div className="App">
      <>
        <button onClick={()=>{setInterval(() => {
          if(count > 0){
            setCount(count => count -1)
          }
        }, 1000);}}>Start Countdown</button>

        <h1>{count}</h1>
      </>
    </div>
  );
}

export default App;
