import React from 'react';
import {useEffect} from 'react';

import './App.css';
import SearchIcon from './search.svg';
// 9eb546a5
const API_URL = 'http://www.omdbapi.com/?apikey=9eb546a5';

const movie1 = {
    "Title": "Superman & Lois",
    "Year": "2021–2024",
    "imdbID": "tt11192306",
    "Type": "series",
    "Poster": "https://m.media-amazon.com/images/M/MV5BMWU5NmFlYjktMmM1NS00NGMwLWIzN2MtMWQ2YTg0ZWMyNmVjXkEyXkFqcGc@._V1_SX300.jpg"
}

const App = () => {

    const searchMovie = async (title) =>{
        const APIresponse = await fetch(`${API_URL}&s=${title}`);
        const data = await APIresponse.json();
        console.log(data.Search);
    }
    useEffect(() => {
        searchMovie('Superman')
    },[]);
    return (
        <>
        <div className="app">
            <h1>Movie Land</h1>

            <div className='search'>
                <input 
                placeholder='Search for movies' 
                value = 'Superman' 
                onChange={()=>{}}
                />

                <img 
                src = {SearchIcon} 
                alt='Search'
                onClick={()=>{}}
                ></img>
            </div>

            <div className='container'>
                <div className='movie'>
                    <div>
                        <p>{movie1.Year}</p>
                    </div>

                    <div>
                        <img src={movie1.Poster !== 'N/A' ? movie1.Poster : 'https://via.placeholder.com/400'}></img>
                    </div>
                </div>
            </div>
        </div>
        </>
    );
}

export default App;
